import * as signalR from '@microsoft/signalr';
import { MessageDTO } from '../DTO/MessageDTO';
import { Subject } from 'rxjs';
import { environment } from './../../environments/environment';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
export class ChatService {
    constructor(http) {
        this.http = http;
        this.connection = new signalR.HubConnectionBuilder().withUrl(environment.hubConnectionURL)
            .configureLogging(signalR.LogLevel.Information).build();
        this.POST_URL = environment.broadcastURL;
        this.receivedMessageObject = new MessageDTO();
        this.sharedOdj = new Subject();
        this.connection.onclose(async () => {
            await this.start();
        });
        this.connection.on("ReciveOne", (user, message) => { this.mapReceivedMessage(user, message); });
        this.start();
    }
    async start() {
        try {
            await this.connection.start();
            console.log("connected super connected");
        }
        catch (err) {
            console.log(err);
            setTimeout(() => this.start(), 5000);
        }
    }
    mapReceivedMessage(user, message) {
        this.receivedMessageObject.user = user;
        this.receivedMessageObject.msgText = message;
        this.sharedOdj.next(this.receivedMessageObject);
    }
    broadcastMessage(msgDTO) {
        this.http.post(this.POST_URL, msgDTO).subscribe(data => console.log(data));
    }
    retrieveMappedObject() {
        return this.sharedOdj.asObservable();
    }
}
